package Ejerc_clase_ute_6;

public class Cubo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor=6;
		
		double resultado = cubo(valor);
		System.out.println("El cubo de 7.5 es: " + resultado); // llamada
		
		    }

		    public static double cubo (double x) { 
		        return x*x*x;
		    } 

		
		

	}


